import React from 'react';
import Webcam from "react-webcam";
import { useRef, useState, useEffect, useCallback } from "react";

function RealDetection(){

  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  const [stateWebcam, setStateWebcam] = useState(false);

  function change_state_webcam(){
    if(stateWebcam === false){
      setStateWebcam(true);
    }
    else{
      setStateWebcam(false);
    }
  }

  return(
    <div>
      <h1>REAL DETECTION</h1>
      <button onClick={change_state_webcam}>Start detection</button>
      {stateWebcam && (
        <>
          <Webcam
            className="mt-3"
            ref={webcamRef}
          />
          <canvas
            ref={canvasRef}
          />
        </>

      )}
    </div>
  );
}

export default RealDetection;
